import axios from 'axios';

const apiUrl = "http://localhost:5264"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{

    console.log('addTask', name);

    const result=await axios.post(`${apiUrl}/addTodo`,{name,isComplete:true});

    //TODO
    return result.data;
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    //TODO
    const result=await axios.put(`${apiUrl}/updateTodo/${id}?isComplete=${isComplete}`)
    await axios.put(`${apiUrl}/${id}?isComplete=${isComplete}`)
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result=await axios.delete(`${apiUrl}/deleteTodo/${id}`,id);
    return result.data;
  }
};
