  // },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    //TODO
    const result=await axios.put(`${apiUrl}/updateTodo/{id}`,isComplete)
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result=await axios.delete(`${apiUrl}/deleteTodo`,id);
    return result.data;
  }
};
